\al{
\frac{\d |W_{ik}|}{\d t}
&amp;= \ub{(1-\norm{W_i}^2)|W_{ik}|}_\text{feature benefit} - \ub{\lambda\1[W_{ik} \ne 0]}_\text{regularization}\tag{1}\\
&amp;= \begin{cases}
\ub{\ps{1-\norm{W_i}^2}}_\text{constant in $k$}\ub{\p{\abs{W_{ik}}-\theta}}_\text{distance from threshold} &amp; \text{if $W_{ik} \ne 0$}\\
0 &amp; \text{otherwise.}
\end{cases}
