\al{
\ps{-\ReLU(W_i \cdot W_j) W_j} \cdot W_j + \ps{-\ReLU(W_i \cdot W_j) W_i} \cdot W_i
&amp;= -(W_i \cdot W_j)\ps{\norm{W_i}^2 + \norm{W_j}^2}\\
&amp;= -\Theta\P{W_i \cdot W_j}
}
